function getCartFromStorage(t){var e=JSON.parse(localStorage.getItem("cart"));console.log(e);var i=e.map(function(t){return'<div class= "shopping-cart__put__item"><img class="shopping-cart__put__item__item-image" src='+t.imgSrc+' alt=""><h2  class="shopping-cart__put__item__item-name">'+t.description+'</h2><h3  class="shopping-cart__put__item__item-price">$'+t.price+'</h3><input class="shopping-cart__put__item__count" type="number" value="0" min="0" max="50"> </div>'}).join("");t.innerHTML=i}var shoppingCartDOM=document.querySelector(".shopping-cart__put");getCartFromStorage(shoppingCartDOM);